--OBX

CREATE VIEW [dbo].[v_ccr_lab_obx]
AS
SELECT   A.empi ,
         B.reportNo [FILLERORDERNO], --Char(100) 流水号 标识一份检验报告
         B.reportNo [SETID], --Char(50) 明细序号 标识一份检验报告中的一个细项
         '' [VISITNUMBER], --Char(100) 就诊唯一号 标识一次门诊，或一次住院，通常是一个挂号流水号或住院流水号
            visitNo [PATIENTID], --Char(100) 病人ID 业务系统标识一个病人的ID，如卡号，病案号等
           C.ORDERITEMCODE [OBSERVATIONID] , --Char(100) 观察项目id 检验明细项目，如RBC(红细胞)、WBC(白细胞)
           ORDERITEMNAME  [OBSERVATION], --Char(50) 观察项目 检验明细项目，如红细胞、白细胞
           RESULT [OBSERVATIONVALUE], --Char(100) 检验结果 检验明细项目的值，如阴性、阳性、具体的数值等
            [NUMERICOBSERVATIONVALUE] = CASE
			WHEN eResearch.dbo.TryPraseNumber(ISNULL(RESULT,''),-999)=-999 THEN 1
			ELSE eResearch.dbo.TryPraseNumber(ISNULL(RESULT,''),-999) END , --Decimal 检验结果数字值 检验明细项目的值，数值部分
			[UNIT] , --Char(100) 单位 检验结果值的单位
            [REFERENCERANGES] , --Char(100) 参考范围 检验明细项目的正常参考范围
            [ABNORMALFLAGS] = CASE UNCOMMONFLAG WHEN '低' THEN '1' WHEN '高' THEN '2' ELSE '' END , --Char(20) 异常标志 标志结果是否异常
           ORDERITEMCODE [UNIVERSALSERVICEID], --Char(100) 检查项目代码 检查的具体项目
           ORDERITEMNAME [UNIVERSALSERVICENAME], --Char(100) 检查项目名称 检查的具体项目
           RESULTTIME [OBSERVATIONDTTM], --Date 检查日期 执行检查的具体日期
           '' [PRINCIPALRESULTINTERPRETERNO] , --Char(50) 审核医生代码
           '' [PRINCIPALRESULTINTERPRETERNAME], --Char(50) 审核医生名称
           '' [FINALRESULTDTTM] --Date 审核日期
    FROM    DW..PIX A WITH ( NOLOCK )
			JOIN ODS..LISSQ B ON A.patientid = B.visitNo
			JOIN ODS..ORIGIN_LISMX C ON B.reportNo = C.reportNo

