CREATE VIEW [dbo].[v_ccr_imagereport]
AS
SELECT   C.empi,
         A.reportNo [FILLERORDERNO] ,
         A.reportNo AS reportid ,
         A.reportNo AS reportseqid ,
    [VISITNUMBER] ,
    A.[PATIENTID] ,
    '' [ORDERINGFACILITY] ,
    A.reportNo [PLACERORDERNO] ,
    diagnosisNo [DIAGSVCSECTID] ,
    diagnosePrompt [DIAGSVCSECTNAME] ,
    '' [RELEVANTCLINICALINFO] ,
    reportTypeCode [ORDERINGSERVICEUNITID] ,
    A.reportType [ORDERINGSERVICEUNITNAME] ,
    '' [ORDERINGPROVIDERNO] ,
    '' [ORDERINGPROVIDERNAME] ,
    reportTypeCode [UNIVERSALSERVICEID] ,
    A.reportType [UNIVERSALSERVICENAME] ,
    reportDate [OBSERVATIONDTTM] ,
    reportDoctorCode [PRINCIPALRESULTINTERPRETERNO] ,
    reportDoctorName [PRINCIPALRESULTINTERPRETERNAME] ,
    '' [IMAGINGFINDINGS] ,
    reportView  [IMAGINGCONCLUSION] ,
    reportTime [FINALRESULTDTTM] ,
    A.reportContent [DIAGNOSTICIMAGINGTECH] ,
    ''  [BODYPART] ,
    '' AS studyinstanceuid ,
    '' AS aceesionnumber ,
    CASE WHEN A.reportType LIKE '%心电%' THEN '心电'
    WHEN A.reportType LIKE '%US%' THEN 'US'
    WHEN A.reportType LIKE '%CT%' THEN 'CT'
    WHEN A.reportType LIKE '%MR%' THEN 'MR'
    WHEN A.reportType LIKE '%病理%' THEN '病理'
    WHEN A.reportType LIKE '%彩超%' THEN '彩超'
    WHEN A.reportType LIKE '%内镜%' THEN '内镜'
    WHEN A.reportType LIKE '%DR%' THEN 'DR'
    WHEN A.reportType LIKE '%磁共振%' THEN 'MR'
    WHEN A.reportType LIKE '%超声%' THEN '超声'
    WHEN A.reportType LIKE '%B超%' THEN '超声'
    WHEN A.reportType LIKE '%普放%' THEN '普放'
    WHEN A.reportType LIKE '%CR%' THEN 'CR'
    WHEN A.reportType LIKE '%DX%' THEN 'DX'
    WHEN A.reportType LIKE '%ES%' THEN 'ES'
    WHEN A.reportType LIKE '%RF%' THEN 'RF'
    ELSE '其他'
END AS Category
    FROM    ODS..PACSSQ A (NOLOCK)
	JOIN ODS..PACSMX B ON A.reportNo = B.reportNO
	JOIN DW..PIX C ON A.PATIENTID = C.patientid



