CREATE PROCEDURE [dbo].[sp_qc_generateqcdata] @table_id NVARCHAR(50)      --dm_tb表ID
AS
BEGIN

BEGIN TRY

BEGIN TRAN;

            DECLARE @table_name NVARCHAR(50);
            DECLARE @view_name NVARCHAR(50);
			DECLARE @primary_key NVARCHAR(200);
            DECLARE @patient_tb_flag INT;
            DECLARE @encounter_tb_flag INT;
            DECLARE @date_field NVARCHAR(50);
            DECLARE @source_system NVARCHAR(50);

SELECT TOP 1
                    @table_name = [name] ,
					@primary_key = primary_key,
                    @patient_tb_flag = patient_tb_flag ,
                    @encounter_tb_flag = encounter_tb_flag ,
                    @date_field = date_field ,
                    @source_system = source_system
FROM    dm_tb
WHERE   id = @table_id;

PRINT @patient_tb_flag;
            PRINT @encounter_tb_flag;

            SET @view_name = 'v_' + @table_name;

			--qc_rpt_gen
			DECLARE @sql_rpt_gen NVARCHAR(MAX);
			SET @sql_rpt_gen = 'INSERT INTO dbo.qc_rpt_gen
			        ( dm_tb_id ,
			          row_count,
					  repeat_count
			        )
			SELECT
			'''+@table_id+''',
			(SELECT COUNT(1) FROM '+@view_name+'),
			(SELECT ISNULL(SUM(repeat_count),0) AS repeat_count FROM (SELECT COUNT(1) AS repeat_count FROM '+@view_name+' GROUP BY '+@primary_key+' HAVING COUNT(1) > 1) AS t)';
			PRINT @sql_rpt_gen;
			DELETE dbo.qc_rpt_gen WHERE dm_tb_id = @table_id;
EXECUTE (@sql_rpt_gen);

--qc_rpt_dis
DECLARE @patient_view_name NVARCHAR(100);
SELECT TOP 1
                    @patient_view_name = 'v_' + name
FROM    dbo.dm_tb
WHERE   patient_tb_flag = 1;

DECLARE @encounter_view_name NVARCHAR(100);
SELECT TOP 1
                    @encounter_view_name = 'v_' + name
FROM    dbo.dm_tb
WHERE   encounter_tb_flag = 1;

DECLARE @sql_rpt_dis NVARCHAR(MAX);
            SET @sql_rpt_dis = 'insert into qc_rpt_dis (dm_tb_id,year,month,row_count,patient_rel_count,encounter_rel_count)
			SELECT  ''' + @table_id + ''',
		DATEPART(YEAR, ISNULL(t1.' + @date_field
                + ',''1900-01-01'')) AS [year] ,
        DATEPART(MONTH, ISNULL(t1.' + @date_field
                + ',''1900-01-01'')) AS [month] ,
        COUNT(1) AS [row_count] ,
        COUNT(CASE WHEN t2.patientid IS NOT NULL THEN 1
                   ELSE NULL
              END) AS [patient_rel_count] ,
        COUNT(CASE WHEN t3.visitnumber IS NOT NULL THEN 1
                   ELSE NULL
              END) AS [encounter_rel_count]
FROM    ' + @view_name + ' t1
        LEFT JOIN ( '
                + ( CASE WHEN @patient_tb_flag = 1
                         THEN 'select ''-1'' as patientid'
                         ELSE 'SELECT patientid FROM ' + @patient_view_name
                              + ' GROUP BY patientid'
                    END ) + '
                  ) t2 ON t2.patientid = t1.patientid
        LEFT JOIN ( '
                + ( CASE WHEN @patient_tb_flag = 1
                              OR @encounter_tb_flag = 1
                         THEN 'select ''-1'' as visitnumber'
                         ELSE 'SELECT visitnumber FROM '
                              + @encounter_view_name + ' GROUP BY visitnumber'
                    END ) + '
                  ) t3 ON ' + ( CASE WHEN @patient_tb_flag = 1 THEN '1=2'
                                     ELSE 't3.visitnumber = t1.visitnumber'
                                END ) + '
GROUP BY DATEPART(YEAR, ISNULL(t1.' + @date_field + ',''1900-01-01'')) ,
        DATEPART(MONTH, ISNULL(t1.' + @date_field + ',''1900-01-01''))
ORDER BY DATEPART(YEAR, ISNULL(t1.' + @date_field + ',''1900-01-01'')) ,
        DATEPART(MONTH, ISNULL(t1.' + @date_field + ',''1900-01-01''))';

            PRINT @sql_rpt_dis;

            DELETE  dbo.qc_rpt_dis
            WHERE   dm_tb_id = @table_id;
EXECUTE (@sql_rpt_dis);

--qc_rpt_col
DECLARE @sql_rpt_col NVARCHAR(MAX);
            DECLARE @rowcount BIGINT;
SELECT  @rowcount = SUM(row_count)
FROM    dbo.qc_rpt_dis
WHERE   dm_tb_id = @table_id;
IF @rowcount > 0
BEGIN

                    DELETE  dbo.qc_rpt_col
                    WHERE   dm_tb_id = @table_id;

SELECT  id ,
        field ,
        field_name ,
        data_type
INTO    #col_info
FROM    dbo.dm_tb_col
WHERE   table_id = @table_id
ORDER BY id;
WHILE ( SELECT  COUNT(1)
        FROM    #col_info
            ) > 0
BEGIN
                            DECLARE @col_id NVARCHAR(50) ,
                                @col_field_name NVARCHAR(50) ,
                                @col_field_cname NVARCHAR(50) ,
                                @col_db_type NVARCHAR(50);
SELECT TOP 1
                                    @col_id = id ,
        @col_field_name = field ,
       @col_field_cname = field_name ,
       @col_db_type = data_type
FROM    #col_info
ORDER BY id;

SET @sql_rpt_col = 'insert into qc_rpt_col (dm_tb_id,[col_name],col_cname,notnull_count)
		select ''' + @table_id + ''',N''' + @col_field_name
                                + ''' as [col_name],N''' + @col_field_cname
                                + ''' as col_cname,
		count(case when ' + @col_field_name + ' is not null'
                                + ( CASE WHEN @col_db_type = 'Char'
                                         THEN ' and LTRIM(RTRIM(CAST('
                                              + @col_field_name
                                              + ' AS NVARCHAR(MAX)))) <> '''''
                                         ELSE ''
                                    END )
                                + ' then 1 else null end) as notnull_count from '
                                + @view_name;

                            PRINT @sql_rpt_col;
EXECUTE (@sql_rpt_col);

DELETE  #col_info
                            WHERE   id = @col_id;

END;
DROP TABLE #col_info;

END;

COMMIT TRAN;

END TRY
BEGIN CATCH

ROLLBACK TRAN;

            DECLARE @ErrorMessage NVARCHAR(MAX) ,
                @ErrorSeverity INT ,
                @ErrorState INT;
            SET @ErrorMessage = ERROR_MESSAGE();
            SET @ErrorSeverity = ERROR_SEVERITY();
            SET @ErrorState = ERROR_STATE();

            RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);

END CATCH;

END;
